import React, {Component} from 'react';
import { connect } from 'react-redux';
import WithRestoService from '../hoc/';
import Spinner from '../spinner';
import Error from '../error';
import {menuLoaded, menuRequested, menuError, addedToCart} from '../../actions';
import {
    Card, CardText, CardBody, Button,
    CardTitle, CardImg
  } from 'reactstrap';

import './itemPage.css';



class ItemPage extends Component {

    componentDidMount() {
        if( this.props.menuItems.length === 0){
            this.props.menuRequested();

            const {RestoService} = this.props;
            RestoService.getMenuItems()
                .then(res => this.props.menuLoaded(res))
                .catch(error => this.props.menuError());
        }
    }

    render() {
        const {loading, error, menuItems} = this.props;
        if(error) {
            return (
                <div className = "item_page">
                    <Error/>
                </div>
            )
        }
        if(loading) {
            return (
                <div className = "item_page">
                    <Spinner/>
                </div>
            )
        }
        const item = menuItems.find(el => +el.id === +this.props.match.params.id)
        const{title, url, category, price, id} = item;
        //console.log(this.props.menuItems)


        return (
            <div className = "item_page">
                <div className="menu__item item_block">

                    <div className="menu__category"><span>{category}</span></div>
                    <div className="menu__price">Price: <span>{price}$</span></div>
                    {/* <button onClick = {()=>this.props.addedToCart(id)} className="menu__btn">Add to cart</button> */}

                    <span className = {`menu__category_Img ${category}`}></span> 
                </div>
            </div>
        );
    }
}




const mapStateToProps =  (state) =>{
    return {
        menuItems: state.menu,
        loading: state.loading,
        error: state.error
    }
}


const mapDispatchToProps = {
    menuLoaded: menuLoaded,
    menuRequested,
    menuError,
    addedToCart
}


export default WithRestoService ()( connect(mapStateToProps, mapDispatchToProps)(ItemPage) );
